mapper.select:

SELECT COUNT(*) FROM `transaction` `tr`
WHERE `tr`.`timestamp` >= (NOW() - INTERVAL 1 DAY)
AND `tr`.`timestamp` <= NOW() AND `tr`.`chain_id`=#{chainId} ;

SELECT COUNT(0)
FROM (
  SELECT DISTINCT `tr`.`block_number`
  FROM `transaction` `tr`
  WHERE tr.`chain_id` = #{chainId}
) `tmp`;
